// Register
    document.getElementById("showPassword").addEventListener("change", function() {
        let passwordFields = document.querySelectorAll("#password, #password_confirmation");
        passwordFields.forEach(field => {
            field.type = this.checked ? "text" : "password";
        });
    });

    document.getElementById("password").addEventListener("input", function() {
        let password = this.value;
        let passwordHelp = document.getElementById("passwordHelp");
        let passwordSuccess = document.getElementById("passwordSuccess");

        let hasUpperCase = /[A-Z]/.test(password);
        let hasLowerCase = /[a-z]/.test(password);
        let hasNumber = /[0-9]/.test(password);
        let isLongEnough = password.length >= 8;

        if (hasUpperCase && hasLowerCase && hasNumber && isLongEnough) {
            passwordHelp.classList.add("d-none"); // Sembunyikan pesan error
            passwordSuccess.classList.remove("d-none"); // Tampilkan pesan sukses
        } else {
            passwordHelp.classList.remove("d-none"); // Tampilkan pesan error
            passwordSuccess.classList.add("d-none"); // Sembunyikan pesan sukses
        }
    });

//Disable Tanggal Terbooking

document.addEventListener("DOMContentLoaded", function() {
    let bookedDates = JSON.parse(document.getElementById('bookedDates').textContent);
    let calendar = document.getElementById("dates");

    bookedDates.forEach(date => {
        let dateElement = document.querySelector(`[data-date="${date}"]`);
        if (dateElement) {
            dateElement.style.backgroundColor = "red"; // Tandai tanggal yang sudah dibooking
            dateElement.style.pointerEvents = "none"; // Nonaktifkan klik
        }
    });
});

